import React, { Component } from 'react'
import Header from './Header'
import Submit from './Submit'

class Todo extends Component {
    constructor(){
        super()
        this.state = {
            // tasks : ['school', 'home', 'work', "prog"]
        }
    }

    handleTodoChange = (event) => {
        this.setState({
            makeTodo: event.target.value 
        })
    }

    render() {
        // const todos = this.state.tasks.map((task, index)=>{
        //     return <h1 key={index}>{task}</h1>   
        // })
        return (
            <div>
                <Header debug = {this.state.debug} />
                {/* {todos} */}
                <Submit handleTodoChange = {this.handleTodoChange} makeTodo = {this.state.makeTodo}/>
            </div>
        )
    }
}

export default Todo;
