import React, { Component } from 'react'
import { Button } from 'react-bootstrap'
import { v4 as id } from 'uuid';
import Header from './Header'
import Submit from './Submit'
import TodoElement from './TodoElement'

class Todo extends Component {
    constructor() {
        super()
        this.state = {
            tasks: [
                {
                    id: id(),
                    name: "school"
                },
                {
                    id: id(),
                    name: "home"
                }, {
                    id: id(),
                    name: "prog"
                }
            ],
            makeTodo: ''
        }
    }

    handleTodoChange = (e) => {
        e.preventDefault()
        this.setState({
            tasks: this.state.tasks,
            makeTodo: e.target.value
        })
        console.log("changed")
    }

    createTodo = (e) => {
        e.preventDefault()
        if(this.state.makeTodo === "") return false;
        this.setState({
            tasks: [
                ...this.state.tasks,
                {
                    id: id(),
                    name: this.state.makeTodo
                }
            ],
            makeTodo:""
        })
        console.log("created")
    }

    deleteTodo = id => {
        console.log("delet")
        console.log(id)
        this.setState({tasks: [...this.state.tasks.filter(tasks => tasks.id !== id)]})

    }

    render() {
        const todos = this.state.tasks.map((tasks, index) => {
            return (
                <TodoElement tasks={tasks} index={index}/>
            )
        })
        return (
            <div >
                <Header debug={this.state.debug} />
                {todos}
                <Submit handleTodoChange={this.handleTodoChange} makeTodo={this.state.makeTodo} createTodo={this.createTodo} />
            </div>
        )
    }
}

const buttonStyle = { 
    display: 'float-right',
    verticalAlign: 'middle',
    margin:'10px',
    marginRight:'150px',
    background:"#50fa7b",
    color:"#282a36",
    border:"#50fa7b"
}

const textStyle = {
    marginLeft:"150px",
    color:"#50fa7b"
}


export default Todo;
