import React, { Component } from 'react'
import { v4 as id } from 'uuid';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom'
import firebase from '../firebase'
import Auth from './Auth'
import Header from './Header'
import Submit from './Submit'
import TodoElement from './TodoElement'

const db = firebase.firestore()

class Todo extends Component {
    constructor() {
        super()
        this.state = {
            tasks: [
            ],
            makeTodo: ''
        }
    }

    //updates the state from firebase
    componentDidMount() {
        console.log('mounted')
        const retrieved = []
        db.collection("todos").get().then((snapshot) => {
            snapshot.forEach((doc) => {
                if(doc.data().user==="me"){
                    console.log(doc.data());
                    retrieved.push(doc.data())
                    console.log(retrieved)
                }
            });
            this.setState({
                tasks:[...retrieved]
            })
        });
    }
    //updates state with value in the input prior to submission
    handleTodoChange = (e) => {
        e.preventDefault()
        this.setState({
            tasks: this.state.tasks,
            makeTodo: e.target.value
        })
        console.log("changed")
    }

    //creates a todo by adding it to the state and displays it
    createTodo = (e) => {
        e.preventDefault()
        if (this.state.makeTodo === "") return false;

        //adds to db
        const taskId = id()
        db.collection('todos').doc(taskId).set({
            user:'me',
            id: taskId,
            name: this.state.makeTodo
        })
        this.setState({
            tasks: [
                ...this.state.tasks,
                {
                    user:'me' ,
                    id: taskId,
                    name: this.state.makeTodo
                }
            ],
            makeTodo: ""
        })
        console.log("created")
    }

    //removes todo from state
    deleteTodo = id => {
        this.setState({ tasks: [...this.state.tasks.filter(tasks => tasks.id !== id)] })

        //deletes from db
        db.collection('todos').doc(id).delete().catch(error=> {console.error("Deletion error", error)})

    }

    authenticate = () => {
        const provider = new firebase.auth.GoogleAuthProvider();
        firebase.auth().signInWithPopup(provider).then(function(result) {
            // This gives you a Google Access Token. You can use it to access the Google API.
            var token = result.credential.accessToken;
            console.log(token)
            // The signed-in user info.
            var user = result.user;
            console.log(user.uid)
            // ...
          }).catch(function(error) {
            // Handle Errors here.
            var errorCode = error.code;
            var errorMessage = error.message;
            // The email of the user's account used.
            var email = error.email;
            // The firebase.auth.AuthCredential type that was used.
            var credential = error.credential;
            // ...
          });
    }


    render() {

        //creates all the todos to be rendered
        const todos = this.state.tasks.map((tasks, index) => {
            return (
                <TodoElement tasks={tasks} key={index} deleteTodo={this.deleteTodo} />
            )
        })

        //send the rendered version of the todos and the submit
        return (
            <div >
                <Header debug={this.state.debug} />
                <Router>
                    <Route path="/todos">
                        {todos}
                        <Submit handleTodoChange={this.handleTodoChange} makeTodo={this.state.makeTodo} createTodo={this.createTodo} />
                    </Route>
                    <Route path='/auth'>
                        <h2>yeehaw</h2>
                        <Auth authenticate={this.authenticate}/>
                    </Route>

                </Router>
            </div>
        )
    }
}



export default Todo;
