import React, { Component } from 'react'
import Header from './Header'
import Submit from './Submit'

class Todo extends Component {
    constructor(){
        super()
        this.state = {
             tasks : ['school', 'home', 'work', "prog"],
             makeTodo: ''
        }
    }

    handleTodoChange = (e) => {
        this.setState({
            tasks: this.state.tasks,
            makeTodo: event.target.value 
        })
        console.log("changed")
    }

    createTodo = (e) => {
        preventDefault()
        this.setState({
            tasks: [...this.state.tasks, this.state.makeTodo] 
        })
        console.log("created")
    }

    render() {
         const todos = this.state.tasks.map((task, index)=>{
             return <h1 key={index}>{task}</h1>   
         })
        return (
            <div>
                <Header debug = {this.state.debug} />
                {todos}
                <Submit handleTodoChange = {this.handleTodoChange} makeTodo = {this.state.makeTodo} createTodo={this.createTodo}/>
            </div>
        )
    }
}

export default Todo;
