import React, { Component } from 'react'
import { Button } from 'react-bootstrap'
import Header from './Header'
import Submit from './Submit'

class Todo extends Component {
    constructor() {
        super()
        this.state = {
            tasks: [
                {
                    id: 0,
                    name: "school"
                },
                {
                    id: 1,
                    name: "home"
                }, {
                    id: 2,
                    name: "prog"
                }
            ],
            makeTodo: ''
        }
    }

    handleTodoChange = (e) => {
        e.preventDefault()
        this.setState({
            tasks: this.state.tasks,
            makeTodo: e.target.value
        })
        console.log("changed")
    }

    createTodo = (e) => {
        e.preventDefault()
        this.setState({
            tasks: [
                ...this.state.tasks,
                {
                    id: 4,
                    name: this.state.makeTodo
                }
            ]
        })
        console.log("created")
    }

    deleteTodo = id => {
        console.log("delet")
        console.log(id)
        this.setState({tasks: [...this.state.tasks.filter(tasks => tasks.id !== id)]})

    }

    render() {
        const todos = this.state.tasks.map((tasks, index) => {
            return (
                <div key={index}>
                    <h1 >{tasks.name}</h1>
                    <Button onClick={() => this.deleteTodo(tasks.id)}>Delete</Button>
                </div>
            )
        })
        return (
            <div>
                <Header debug={this.state.debug} />
                {todos}
                <Submit handleTodoChange={this.handleTodoChange} makeTodo={this.state.makeTodo} createTodo={this.createTodo} />
            </div>
        )
    }
}

const elementStyle = {
    display: 'inline-block'
}


export default Todo;
