import React, { Component } from 'react'
import { Button } from 'react-bootstrap'
import { v4 as id } from 'uuid';
import Header from './Header'
import Submit from './Submit'

class Todo extends Component {
    constructor() {
        super()
        this.state = {
            tasks: [
                {
                    id: id(),
                    name: "school"
                },
                {
                    id: id(),
                    name: "home"
                }, {
                    id: id(),
                    name: "prog"
                }
            ],
            makeTodo: ''
        }
    }

    handleTodoChange = (e) => {
        e.preventDefault()
        this.setState({
            tasks: this.state.tasks,
            makeTodo: e.target.value
        })
        console.log("changed")
    }

    createTodo = (e) => {
        e.preventDefault()
        console.log(e.target)
        this.setState({
            tasks: [
                ...this.state.tasks,
                {
                    id: id(),
                    name: this.state.makeTodo
                }
            ],
            makeTodo:""
        })
        console.log("created")
    }

    deleteTodo = id => {
        console.log("delet")
        console.log(id)
        this.setState({tasks: [...this.state.tasks.filter(tasks => tasks.id !== id)]})

    }

    render() {
        const todos = this.state.tasks.map((tasks, index) => {
            return (
                <div key={index} className="row" >
                    <h1 style = {{marginLeft:'50px'}} >{tasks.name}</h1>
                    <Button onClick={() => this.deleteTodo(tasks.id)} style={elementStyle}>Delete</Button>
                </div>
            )
        })
        return (
            <div >
                <Header debug={this.state.debug} />
                {todos}
                <Submit handleTodoChange={this.handleTodoChange} makeTodo={this.state.makeTodo} createTodo={this.createTodo} />
            </div>
        )
    }
}

const elementStyle = { 
    align: "right",
    float:"right"
}


export default Todo;
