import React, { Component } from 'react'
import { v4 as id } from 'uuid';
import firebase from '../firebase'
import Header from './Header'
import Submit from './Submit'
import TodoElement from './TodoElement'

class Todo extends Component {
    constructor() {
        super()
        this.state = {
            tasks: [
            ],
            makeTodo: ''
        }
    }

    handleTodoChange = (e) => {
        e.preventDefault()
        this.setState({
            tasks: this.state.tasks,
            makeTodo: e.target.value
        })
        console.log("changed")
    }

    createTodo = (e) => {
        e.preventDefault()
        if (this.state.makeTodo === "") return false;
        const db = firebase.firestore()
        db.collection('todos').add({
            id: id(),
            name: this.state.makeTodo
        })
        this.setState({
            tasks: [
                ...this.state.tasks,
                {
                    id: id(),
                    name: this.state.makeTodo
                }
            ],
            makeTodo: ""
        })
        console.log("created")
    }

    deleteTodo = id => {
        console.log("delet")
        console.log(id)
        this.setState({ tasks: [...this.state.tasks.filter(tasks => tasks.id !== id)] })

    }

    render() {
        const todos = this.state.tasks.map((tasks, index) => {
            return (
                <TodoElement tasks={tasks} index={index} deleteTodo={this.deleteTodo} />
            )
        })
        return (
            <div >
                <Header debug={this.state.debug} />
                {todos}
                <Submit handleTodoChange={this.handleTodoChange} makeTodo={this.state.makeTodo} createTodo={this.createTodo} />
            </div>
        )
    }
}



export default Todo;
