import React, { Component } from 'react'
import Header from './Header'
import Submit from './Submit'

class Todo extends Component {
    constructor() {
        super()
        this.state = {
            tasks: [
                {
                    id: 0,
                    task: "school"
                }
            ],
            makeTodo: ''
        }
    }

    handleTodoChange = (e) => {
        e.preventDefault()
        this.setState({
            tasks: this.state.tasks,
            makeTodo: e.target.value
        })
        console.log("changed")
    }

    createTodo = (e) => {
        e.preventDefault()
        this.setState({
            tasks: [...this.state.tasks, this.state.makeTodo]
        })
        console.log("created")
    }

    deleteTodo = e => {
        e.preventDefault()
        console.log("delet")
        this.setState()
    }

    render() {
        const todos = this.state.tasks.map((task, index) => {
            return (
                <div>
                    <h1 key={index}>{task}</h1>
                    <button onClick={this.deleteTodo}>Delete</button>
                </div>
            )
        })
        return (
            <div>
                <Header debug={this.state.debug} />
                {todos}
                <Submit handleTodoChange={this.handleTodoChange} makeTodo={this.state.makeTodo} createTodo={this.createTodo} />
            </div>
        )
    }
}

export default Todo;
